css:
    .print-format{

         padding:0.50in !important;
    }


@media print{
		    thead {display: table-row-group;}
			}
			.ql-editor li  {
			   font-size: 11px;
			}
			.tax-break-up table {
			        margin: 0px 0px;
			        font-size:11px;

			}

			.print-format{
            padding:0.50in !important;
            font-size:12px !important;
            }
            .print-format p{
                margin:3px 0px 0px 0px;
            }
			body{
				margin: 0 auto;
				background-color: #fff;
				font-family: 'Ubuntu', sans-serif;
				font-size: 11px !important;
				line-height: 1.3;
				margin:0px auto;
			}
			.main_div{
				padding: 15px 0px;
			   /* margin:0px auto 0 auto;*/
			    margin-top:-40px;
				width: 100%;
			}
			.main_heading{
				font-weight: 700;
				text-transform: uppercase;
				margin:0;
				line-height: 1.3;
			}
			.mb0{margin-bottom: 0px !important;}
			.mt0{margin-top: 0px !important}
			.pb0{padding-bottom: 0px;}
			.mt5{margin-top: 5px;}
			.mb5{margin-bottom: 5px;}
			table tr td{padding: 3px 5px !important;}
			.print-format td, .print-format th {
                vertical-align: top !important;
                padding: 3px 5px !important;
            }
			table tr td p{
				margin:0;
				margin-top:2px;
				line-height: 1.3;
			}
			table tr th{
				font-weight: 400;
    		background-color: #d5d5d5;
    		padding: 5px !important;
			}
			.para_heading{
				font-size: 1.05em;
			}
			.bold{
				font-weight: 700;
			}
			.sign_div{
				height: 20px;
			}
			.hr{
		    margin-top: 7px;
		    margin-bottom: 7px;
		    border-color: #d1d8dd;
			}


			.outerDiv {

                height: auto;

            }
            .leftDiv {

                height: auto;
                width: 50%;
                float: left;
            }
            .rightDiv {

                height: auto;
                width: 50%;
                float: right;
            }
