css:
@media print{
		    thead {display: table-row-group;}
			}
			body{
				margin: 0 auto;
				background-color: #fff;
				font-family: 'Ubuntu', sans-serif;
				font-size: 13px;
				line-height: 1.3;
			}
			.main_div{
				padding: 15px 0px;
				max-width:1000px;
				margin:30px auto 0 auto;
			}
			.main_heading{
				font-weight: 700;
				text-transform: uppercase;
				margin:0;
				line-height: 1.3;
			}
			.mb0{margin-bottom: 0px;}
			.mt0{margin-top: 0px}
			.pb0{padding-bottom: 0px;}
			.mt5{margin-top: 5px;}
			.mb5{margin-bottom: 5px;}
			table tr td{padding: 3px 5px !important;}
			table tr td p{
				margin:0;
				margin-top:2px;
				line-height: 1.3;
			}
			table tr th{
				font-weight: 400;
    		background-color: #d5d5d5;
    		padding: 5px !important;
			}
			.para_heading{
				font-size: 1.15em;
			}
			.bold{
				font-weight: 700;
			}
			.sign_div{
				height: 30px;
			}
			.hr{
		    margin-top: 7px;
		    margin-bottom: 7px;
		    border-color: #d1d8dd;
			}
